import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Membre from './components /Membre';

const team = {
  membre1:{
  nom: Soufian,
  age:25,
},

membre2:{
  nom: Oliver,
  age:15,
},
membre3:{
  nom: Fouz,
  age:19,
},
membre1:{
  nom: Antho,
  age:16,
},
membre1:{
  nom: Sam,
  age:8 ,
}
}
class App extends Component {
  state = {
    team
  }

  handleClick = () => {
    const team = {... this.state.team}
    team.membre1.age += 1
    this.setState ({team})
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>Voici la liste des membres du groupe</p>
          <Membre nom ={team}/>
          <Membre nom ="Sami"/>
          <Membre nom ="Oliver" children=''>
          Joue a fortnite
          </Membre>
          <Membre nom ="Antho"/>
          <Membre nom ="Fouz"/> 
        </header>
      </div>
    ); 
  }
}

export default App;
